mapper.update:
UPDATE
	BREAKER_INFO
SET
	BREAKER_POLICY_CODE = #{breakerPolicyCode}
	, ELEMENT_CODE = #{elementCode}
	, MODIFY_DETAIL = #{modifyDetail}
	, MODIFY_DATE = NOW()
	, MODIFIER = #{modifier}

	, SYSTEM_CONTROL = #{systemControl}

WHERE
	BREAKER_ID = #{breakerId}

mapper.insert:
    	INSERT INTO
    		BREAKER_HISTORY
    		(
				BREAKER_ID
				, BREAKER_POLICY_CODE
				, MODIFIER
				, ELEMENT_CODE
				, MODIFY_DETAIL
				, MODIFY_REQ_DATE
    		)
    		VALUES
    		(
    			#{breakerId}
    			, #{breakerPolicyCode}
    			, #{modifier}
    			, #{elementCode}
    			, #{modifyDetail}
    			, NOW()
    		)
