mapper.update:
UPDATE
	BREAKER_INFO
SET
	BREAKER_POLICY_CODE = #{breakerPolicyCode}
WHERE
	BREAKER_ID = #{breakerId}

mapper.insert:
INSERT INTO
	WEATHER_DATA
(
	BREAKER_ID
	, DIR
	, TMP
	, HM
	, WIND
	, GUST
	, RAIN
	, UV
	, LIGHT
)
VALUES
(
	#{breakerId}
	, #{dir}
	, #{tmp}
	, #{hm}
	, #{wind}
	, #{gust}
	, #{rain}
	, #{uv}
	, #{light}
)
